import React, { Component } from 'react';

import ProfilePicture from '../../../node_modules/profile-picture';
import '../../../node_modules/profile-picture/build/ProfilePicture.css';

import axios from 'axios';

class Profile_Picture extends Component {
  constructor(props) {
    super(props);

    this.profilePictureRef = React.createRef();
  }

  async handleUpload() {
    const PP = this.profilePictureRef.current;
    const imageData = PP.getData();
    const file = imageData.file;
    const imageAsDataURL = PP.getImageAsDataUrl();
    console.log(imageAsDataURL);
    //add here the upload logic...
    const formData = {
      image: imageAsDataURL,
    };
    try {
      await axios.post('/api/users/image_upload', formData);
      console.log('Successfully Uploaded the Image');
    } catch (err) {
      console.error(err);
    }
  }

  render() {
    return (
      <div className='container'>
        <ProfilePicture
          ref={this.profilePictureRef}
          useHelper={true}
          debug={false}
          frameFormat='circle'
          //   onZoomChange={() => {
          //     const PP = this.profilePictureRef.current;
          //     const imageData = PP.getData();
          //     const file = imageData.file;
          //     const imageAsDataURL = PP.getImageAsDataUrl();
          //     this.setState({ image_src: imageAsDataURL });
          //     console.log(this.state.image_src);
          //   }}
        />

        <button
          onClick={() => {
            props.setToggleDisplay(!props.toggleDisplay);
          }}
          className='btn btn-success close'
        >
          Save
        </button>
      </div>
    );
  }
}

export default Profile_Picture;
