import React, { useEffect, Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getPost } from '../../actions/post';
import PostItem from './PostItem';
import Spinner from '../layout/Spinner';
import { Link, withRouter } from 'react-router-dom';
import ListOfPeople from '../posts/ListOfPeople';

const LikePage = ({ getPost, match, post: { post, loading }, history }) => {
  useEffect(() => {
    getPost(match.params.post_id);
  }, [getPost, match]);

  return loading || post === null ? (
    <Spinner />
  ) : (
    <Fragment>
      <Link
        onClick={() => {
          history.goBack();
        }}
        className='btn'
      >
        Go Back
      </Link>
      <PostItem post={post} showActions={false} />

      <p className='lead'>People who like this post :</p>
      <ListOfPeople list={post.likes} />
    </Fragment>
  );
};

LikePage.propTypes = {
  getPost: PropTypes.func.isRequired,
  post: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  post: state.post,
});

export default connect(mapStateToProps, { getPost })(withRouter(LikePage));
