import React, { Fragment, useState } from 'react';
import PropTypes from 'prop-types';
import Avatar from 'react-avatar-edit';
import { Modal, ModalHeader, ModalBody } from 'reactstrap';
import Profile_Picture from './Profile_Picture'

const SetProfileImage = (props) => {
  const [toggleDisplay, setToggleDisplay] = useState(false);
  const [preview, setPreview] = useState(null);
  const [source, setSource] = useState(
    'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTgaNi3D4eNMnyGH56_1gD-0485xuI07N6ztw&usqp=CAU'
  );

  const onClose = () => {
    setPreview(null);
  };

  const onCrop = (preview) => {
    setPreview(preview);
  };

  return (
    <Fragment>
      <div>
        <button
          onClick={(e) => {
            setToggleDisplay(!toggleDisplay);
          }}
          className='btn btn-success'
        >
          Click Me
        </button>
      </div>

      <Modal
        isOpen={toggleDisplay}
        toggle={() => {
          setToggleDisplay(!toggleDisplay);
        }}
      >
        <ModalBody>
          <div className='container-fluid'>
            <div className='row'>
              <div className='col-12 col-md-5'>

              </div>
            </div>
          </div>
        </ModalBody>
      </Modal>
    </Fragment>
  );
};

SetProfileImage.propTypes = {};

export default SetProfileImage;
