import React, { Fragment, useState, Component } from 'react';
import PropTypes from 'prop-types';
import { Modal, ModalHeader, ModalBody } from 'reactstrap';

class setProfileImage extends Component {
  // const [toggleDisplay, setToggleDisplay] = useState(false);
  constructor(props) {
    super(props);

    this.state = {
      toggleDisplay: false,
    };

    this.toggleModal = this.toggleModal.bind(this);
  }

  toggleModal() {
    this.setState({
      toggleDisplay: !this.state.toggleDisplay,
    });
  }

  render() {
    return (
      <div>
        <div>
          <button onClick={this.toggleModal} className='btn btn-success'>
            Click Me
          </button>
        </div>

        <div>
          <Modal isOpen={this.state.toggleDisplay} toggle={this.toggleModal}>
            <ModalHeader
            >Header</ModalHeader>
            <ModalBody>Body</ModalBody>
          </Modal>
        </div>
      </div>
    );
  }
}
setProfileImage.propTypes = {};

export default setProfileImage;
