import React, { useState, useEffect, Fragment } from 'react';
import PropTypes from 'prop-types';
import { Link, withRouter } from 'react-router-dom';
import ProfileData from './ProfileData';
import Spinner from '../layout/Spinner';
import ProfileNotFound from './ProfileNotFound';
import axios from 'axios';
import { getParticularUserAllPosts } from '../../actions/post';
import { connect } from 'react-redux';

const IndividualProfile = ({
  getParticularUserAllPosts,
  match,
  history,
  posts,
}) => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const getProfileByID = async (userID) => {
    try {
      const res = await axios.get(`/api/profile/user/${userID}`);
      setLoading(false);
      setProfile(res.data);
    } catch (err) {
      console.log(err);
      setError(err);
      setLoading(false);
    }
  };

  useEffect(() => {
    getProfileByID(match.params.user_id);
    getParticularUserAllPosts(match.params.user_id);
  }, [getProfileByID, match]);

  return loading && profile === null ? (
    <Spinner />
  ) : profile !== null ? (
    <Fragment>
      {/* <button onClick={() => { history.goBack()}} className='btn btn-light my-1'>
        Click here to go back
      </button> */}
      <Link
        onClick={() => {
          history.goBack();
        }}
        className='btn btn-light my-1'
      >
        Go Back
      </Link>

      <ProfileData profile={profile} posts={posts} />
    </Fragment>
  ) : (
    <Fragment>
      <ProfileNotFound />
    </Fragment>
  );
};

IndividualProfile.propTypes = {
  posts: PropTypes.object.isRequired,
  getParticularUserAllPosts: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  posts: state.post.posts,
});

export default connect(mapStateToProps, { getParticularUserAllPosts })(
  withRouter(IndividualProfile)
);
