import React, { useEffect, Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getAllProfiles } from '../../actions/profile';
import { Link } from 'react-router-dom';

const SearchBar = ({ getAllProfiles, profiles }) => {
  useEffect(() => {
    getAllProfiles();
  }, [getAllProfiles]);

  const matchList = document.getElementById('match-list');
  let matches = [];

  const handleChange = (e) => {
    let profileList = profiles.map((profile) => {
      if (profile.user !== null) {
        return profile.user;
      }
    });

    profileList = profileList.filter((list) => list !== undefined);

    if (e !== '') {
      // Get matches to current text input
      matches = profileList.filter((profile) => {
        const regex = new RegExp(`${e}`, 'gi');
        if (profile.name.match(regex)) {
          return profile;
        }
      });

      // console.log(matches);

      if (e.length === 0) {
        matches = [];
        matchList.innerHTML = '';
      }

      // display_result(matches);
      <div id='match-list'>
        {matches.length > 0 &&
          matches.map((match) => (
            <Fragment>
              <div className='card card-body mb-1'>
                <img className='profile-img' src={match.avatar} />{' '}
                <span>{match.name}</span>
              </div>
            </Fragment>
          ))}
      </div>
    }
  };

  const display_result = (matches) => {
    if (matches.length > 0) {
      console.log(matches);
      const html = matches
        .map(
          (match) => `<div>
        <img className='profile-img' src=${match.avatar} /> <span>${match.name}</span>
        </div>`
        )
        .join('');

      matchList.innerHTML = html;
      console.log(html);
    }
  };

  return (
    <div id='searchWrapper'>
      <div class='input-icons'>
        <i className='fas fa-search icon' />
        <input
          id='searchBar'
          type='text'
          placeholder='Search'
          onChange={(e) => handleChange(e.target.value)}
        />
      </div>

    </div>
  );
};

SearchBar.propTypes = {
  profiles: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  profiles: state.profile.profiles,
});

export default connect(mapStateToProps, { getAllProfiles })(SearchBar);
