import React, { Fragment } from 'react';
import SetProfileImage from './setProfileImage';

const ClickMe = () => {
  const [show, setShow] = useState(false);

  const toggle = (e) => {
    setShow(!show);
  };

  return (
    <Fragment>
      <div>
        <button onClick={toggle}>Click Me</button>
      </div>

      {show && <SetProfileImage />}
    </Fragment>
  );
};

export default ClickMe;
