import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Spinner from '../layout/Spinner';
// import { getCurrentUserAllPosts } from '../../actions/post';

const ProfilePost = ({ posts }) => {
  useEffect(() => {
    getCurrentUserAllPosts();
  }, [getCurrentUserAllPosts]);


  return (
  <div className='profile-posts'>
  <h2 className='text-primary my-1'>Posts</h2>
  {posts === null ? (
    <Spinner />
  ) : (
    posts.map((post) => (
      <div key={post._id} className='p_posts bg-white p-1 my-1'>
        <div>
          {/* <h4>
            <a
              href="#!"
              target='_blank'
              rel='noopener noreferrer'
            >
              {repo.name}
            </a>
          </h4> */}
          <p>{post.text}</p>
        </div>
        <div>
          <ul>
            <li className='badge badge-light'>
              Like: {post.likes.length}
            </li>
            <li className='badge badge-light'>Dislike: {post.dislikes.length}</li>
            <li className='badge badge-light'>Comments: {post.comments.length}</li>
          </ul>
        </div>
      </div>
    ))
  )}
</div>)

};

ProfilePost.propTypes = {
  getCurrentUserAllPosts: PropTypes.func.isRequired,
  posts: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  posts: state.post,
});

export default connect(mapStateToProps, { getCurrentUserAllPosts })(
  ProfilePost
);
