import React, { useEffect, Fragment } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { getCurrentProfile, getAllProfiles } from '../../actions/profile';
import Spinner from '../layout/Spinner';
import Posts from '../posts/Posts';
import SearchBar from '../searchbar/index';
// import AutoComplete from '../searchbar/AutoComplete';

const Dashboard = ({
  getCurrentProfile,
  getAllProfiles,
  auth: { user },
  profile: { profile, profiles, loading },
}) => {
  useEffect(() => {
    getCurrentProfile();
    getAllProfiles();
  }, [getCurrentProfile, getAllProfiles]);

  // let oldList = profiles.map((profile) => {
  //   if (profile.user !== null) {
  //     return profile.user.name;
  //   }
  // });

  // oldList = oldList.filter((list) => list !== undefined);

  // let suggestions = Object.values(oldList);

  // console.log(suggestions);

  return loading && profile === null ? (
    <Spinner />
  ) : (
    <Fragment>
      {/* <SearchBar /> */}
      {/* <SearchActions /> */}
      <AutoComplete suggestions={suggestions} />
      <h1 className='large text-primary'>Dashboard</h1>
      <p className='lead'>
        <i className='fas fa-user'></i> Welcome {user && user.name}
      </p>
      {profile !== null ? (
        <Fragment>
          <Posts />
        </Fragment>
      ) : (
        <Fragment>
          <p>You have not yet setup a profile, please add some info</p>
          <Link to='/create-profile' className='btn btn-primary my-1'>
            Create Profile
          </Link>

          <Posts />
        </Fragment>
      )}
    </Fragment>
  );
};

Dashboard.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  getAllProfiles: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  profile: state.profile,
});

export default connect(mapStateToProps, { getCurrentProfile, getAllProfiles })(
  Dashboard
);
