import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getAllProfiles } from '../../actions/profile';
import { Link } from 'react-router-dom';
import { getProfileByID } from '../../actions/profile';
import { matchedData } from 'express-validator';

const SearchBar = ({ getProfileByID, getAllProfiles, profiles }) => {
  useEffect(() => {
    getAllProfiles();
  }, [getAllProfiles]);

  const handleChange = (e) => {
    let profileList = profiles.map((profile) => {
      if (profile.user !== null) {
        return profile.user;
      }
    });

    profileList = profileList.filter((list) => list !== undefined);

    let matches = [];
    // profileList.map((list) => console.log(list.name));

    if (e !== '') {
      // Get matches to current text input

      matches = profileList.filter((profile) => {
        const regex = new RegExp(`${e}`, 'gi');
        if (profile.name.match(regex)) {
          return profile;
        }
      });

      // console.log(matches);

      if (e.length === 0) {
        matches = [];
      }

      display_result(matches);
    }
  };

  const display_result = (matches) => {
    if (matches.length > 0) {
      const html = matches.map(
        (match) => `<div> 
      <Link to=${getProfileByID(match._id)}>
        <img src=${macth.avatar} /> <span>${macth.name}</span>
      </Link>
        </div>`
      ).join("")
    }
  };

  return (
    <div id='searchWrapper'>
      <div class='input-icons'>
        <i className='fas fa-search icon' />
        <input
          id='searchBar'
          type='text'
          placeholder='Search'
          onChange={(e) => handleChange(e.target.value)}
        />
      </div>
      <div id='match-list'></div>
    </div>
  );
};

SearchBar.propTypes = {
  profiles: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  profiles: state.profile.profiles,
});

export default connect(mapStateToProps, { getAllProfiles, getProfileByID })(
  SearchBar
);
