import * as definedAlertTypes from '../actions/types';

const initialState = [];

export default function (state = initialState, action) {
  switch (action.type) {
    case definedAlertTypes.SET_ALERT:
      return [...state, action.payload];

    case definedAlertTypes.REMOVE_ALERT:
      return state.filter((alert) => alert.id !== action.payload);

    default:
      return state;
  }
}
