import React, { useEffect, useState, Fragment } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const DisplayIndividualAllPosts = ({ match }) => {
  const [posts, setPosts] = useState([]);

  const getParticularUserAllPosts = async (userID) => {
    const res = await axios.get(`/api/posts/user/${userID}`);

    setPosts(res.data);
  };

  useEffect(
    () => {
      getParticularUserAllPosts(match.params.user_id);
    },
    getParticularUserAllPosts,
    match
  );

  return (
    <div className='profile-posts'>
      <h2 className='text-primary my-1'>All Posts of posts</h2>
      {posts.length > 0 && (
        <Fragment>
          {posts.map((post) => (
            <div key={post._id} className='p_posts bg-white p-1 my-1'>
              <div>
                <p>{post.text}</p>
              </div>
              <div>
                <ul>
                  <li className='badge badge-light'>
                    Like: {post.likes.length}
                  </li>
                  <li className='badge badge-light'>
                    Dislike: {post.dislikes.length}
                  </li>
                  <li className='badge badge-light'>
                    <Link to={`/posts/${post._id}`}>
                      Comments: {post.comments.length}
                    </Link>
                  </li>
                </ul>
              </div>
            </div>
          ))}
        </Fragment>
      )}
    </div>
  );
};

export default DisplayIndividualAllPosts;
