import React, { Fragment } from 'react';
import Avatar from 'react-avatar-edit';
import Modal from 'react-responsive-modal';

class setProfileImage extends React.Component {
  constructor(props) {
    super(props);
    const src =
      'https://upload.wikimedia.org/wikipedia/commons/thumb/b/b6/Image_created_with_a_mobile_phone.png/1200px-Image_created_with_a_mobile_phone.png';
    this.state = {
      preview: null,
      src,
      login: false,
    };
    this.onCrop = this.onCrop.bind(this);
    this.onClose = this.onClose.bind(this);
  }

  onOpenModal = () => {
    console.log('Model Opened login True');
    login = true;
  };

  onCloseModal = () => {
    console.log('Model Closed login False');
    this.setState()
  };

  onClose() {
    this.setState({ preview: null });
  }

  onCrop(preview) {
    this.setState({ preview });
  }

  render() {
    return (
      <Fragment>
        <div>
          <button onClick={onOpenModal()}>Click Me</button>
        </div>
        <Modal open={login} onClose={onCloseModal()}>
          <div>
            <Avatar
              width={300}
              height={300}
              onCrop={this.onCrop}
              onClose={this.onClose}
              src={this.state.src}
            />
          </div>
          <div>
            <button className='btn btn-success my-1'>Save</button>
          </div>
        </Modal>
      </Fragment>
    );
  }
}

export default setProfileImage;
