import React, { Fragment, useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { addExperience } from '../../actions/profile';
import { connect } from 'react-redux';
import { Link, withRouter } from 'react-router-dom';
import DisplayExperience from './DisplayExperience';
import Spinner from '../layout/Spinner';
import axios from 'axios';

const AddExperience = ({ addExperience, history }) => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [formData, setFormData] = useState({
    company: '',
    title: '',
    location: '',
    from: '',
    to: '',
    current: false,
    description: '',
  });

  const [toDateDisabled, toggleDisabled] = useState(false);

  const getCurrentProfile = async () => {
    try {
      const res = await axios.get('/api/profile/me');
      setLoading(false);
      setProfile(res.data);
    } catch (err) {
      console.log(err);
      setError(err);
      setLoading(false);
    }
  };

  useEffect(() => {
    getCurrentProfile();
  }, [getCurrentProfile]);

  // const [display_experience, setDisplayExperience] = useState(null);

  const { company, title, location, from, to, current, description } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    addExperience(formData, history);
    setFormData({
      company: '',
      title: '',
      location: '',
      from: '',
      to: '',
      current: false,
      description: '',
    });
  };

  // setDisplayExperience(profile.experience);

  return loading && profile === null ? (
    <Spinner />
  ) : (
    <Fragment>
      <h1 className='large text-primary'>Add an Experience</h1>
      <p className='lead'>
        <i className='fas fa-code-branch'></i> Add any Work Experience that you
        have had in the past
      </p>
      <small>* = required field</small>
      <form className='form' onSubmit={(e) => onSubmit(e)}>
        <div className='form-group'>
          <input
            type='text'
            placeholder='* Title'
            name='title'
            value={title}
            onChange={(e) => onChange(e)}
          />
        </div>

        <div className='form-group'>
          <input
            type='text'
            placeholder='* Company'
            name='company'
            value={company}
            onChange={(e) => onChange(e)}
          />
        </div>

        <div className='form-group'>
          <input
            type='text'
            placeholder='Location'
            name='location'
            value={location}
            onChange={(e) => onChange(e)}
          />
        </div>

        <div className='form-group'>
          <h4>From Date</h4>
          <input
            type='date'
            name='from'
            value={from}
            onChange={(e) => onChange(e)}
          />
        </div>

        <div className='form-group'>
          <h4>
            <input
              type='checkbox'
              name='current'
              checked={current}
              value={current}
              onChange={(e) => {
                setFormData({ ...formData, current: !current });
                toggleDisabled(!toDateDisabled);
              }}
            />{' '}
            Current Job
          </h4>
        </div>

        {!toDateDisabled && (
          <Fragment>
            <div className='form-group'>
              <h4>To Date</h4>
              <input
                type='date'
                name='to'
                value={to}
                onChange={(e) => onChange(e)}
              />
            </div>
          </Fragment>
        )}

        <div className='form-group'>
          <textarea
            placeholder='Job Description'
            name='description'
            value={description}
            onChange={(e) => onChange(e)}
          />
        </div>
        <input type='submit' className='btn btn-primary my-1' />
      </form>

      <h1 className='large text-primary my-2'>Delete an Experience</h1>

      {/* <DisplayExperience experience={profile.experience} /> */}
      {/* {!loading && profile !== null && console.log(profile)} */}
      console.log("Rendered")

      <Link className='btn btn-light my-1' to='/profile'>
        Go Back
      </Link>
    </Fragment>
  );
};

AddExperience.propTypes = {
  addExperience: PropTypes.func.isRequired,
  // profile: PropTypes.object.isRequired,
  // getCurrentProfile: PropTypes.func.isRequired,
};

// const mapStateToProps = (state) => ({
//   profile: state.profile,
// });

export default connect(null, { addExperience })(withRouter(AddExperience));
