import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const SearchBar = ({ profiles }) => {
  const [word, setWord] = useState('');

  const [filterDisplay, setFilterDisplay] = useState(null);

  const handleChange = (e) => {
    let oldList = profiles.map((profile) => {
      if (profile.user !== null) {
        return { name: profile.user.name, avatar: profile.user.avatar };
      }
    });

    oldList = oldList.filter((list) => list !== undefined);

    if (e !== '') {
      // console.log(oldList);

      let newList = [];

      setWord(e);

      newList = oldList.filter((list) => {
        splittedName = list.name.split(' ');
        if (splittedName.length === 1) {
          return splittedName[0].toLowerCase().includes(word.toLowerCase());
        }

        if (splittedName.length > 1) {
          return list.name.toLowerCase().includes(word.toLowerCase());
        }
      });
    } else {
      setFilterDisplay(null);
    }
  };

  return (
    <div id='searchWrapper'>
      <div class='input-icons'>
        <i className='fas fa-search icon' />
        <input
          id='searchBar'
          type='text'
          placeholder='Search'
          onChange={(e) => handleChange(e.target.value)}
        />
      </div>
      <div id='match-list'></div>
    </div>
  );
};

SearchBar.propTypes = {
  profiles: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  profiles: state.profile.profiles,
});

export default connect(mapStateToProps)(SearchBar);
